import React, { useState } from 'react';
import './Navbar.css';
import logo from '../Assets/shop.png';
import cart from '../Assets/cart.png';
import { Link } from 'react-router-dom';

const Navbar = () => {
    const [menu, setMenu] = useState('shop');

    return (
        <div className='navbar'>
            <div className='navbar-logo'>
                <img src={logo} alt='Shop Logo' />
                <p>E-Shop</p>
            </div>
            <ul className='nav-menu'>
                <li onClick={() => setMenu('shop')}>
                    <Link style={{textDecoration:'none'}} to="/">Shop</Link>{menu === 'shop' ? <hr /> : null}
                </li>
                <li onClick={() => setMenu('mans')}>
                    <Link to="/mens">Mans</Link>{menu === 'mans' ? <hr /> : null}
                </li>
                <li onClick={() => setMenu('womans')}>
                    <Link to="/womens">Womens</Link>{menu === 'womans' ? <hr /> : null}
                </li>
                <li onClick={() => setMenu('kids')}>
                    <Link to="/kids">Kids</Link>{menu === 'kids' ? <hr /> : null}
                </li>
            </ul>
            <div className="login-cart">
                <Link to="/login">
                    <button>Login</button>
                </Link>
                <Link to="/cart">
                    <img src={cart} alt="Shopping Cart" />
                </Link>
                <div className='nav-cart-count'>0</div>
            </div>
        </div>
    );
};

export default Navbar;
