import React, { useContext } from 'react';
import '/home/shivam/hiteshChannel/frontend/src/pages/Css/ShopCategory.css';
import { ShopContext } from '../Context/ShopContext';
import dropdown_icon from '../Components/Assets/dropdown.png';
import Item from '../Components/Item/Item';

const ShopCategory = ({ category, banner }) => {
  const { all_product } = useContext(ShopContext);

  if (!all_product || !Array.isArray(all_product)) {
    return <div>Loading products...</div>;
  }

  const filteredProducts = all_product.filter(item => item.category === category);

  return (
    <div className="shop-category">
      <img src={banner} alt="Banner" />
      <div className="shopCategory-indexShort">
        <p>
          <span>Showing 1-{filteredProducts.length} </span> out of {filteredProducts.length} products
        </p>
        <div className="shopCategory-Shop">
          Sort by <img src="dropdown.png" alt="Dropdown" /> {/* Adjust the path */}
        </div>
      </div>
      <div className="ShopCategory-Products">
        {filteredProducts.map(item => (
          <Item
            key={item.id}
            id={item.id}
            name={item.name}
            image={item.image}
            new_price={item.new_price}
            old_price={item.old_price}
          />
        ))}
      </div>
    </div>
  );
};

export default ShopCategory;
